import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { hri } from 'human-readable-ids';

 import { socket } from '../constants/websocket'


const FormContainer = styled.div`
max-width: 500px;
margin: auto;
padding: 20px;
background: rgba(255, 255, 255, 0.8);
border-radius: 10px;
box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h2`
text-align: center;
color: #333;
`;

const Input = styled.input`
width: 100%;
padding: 10px;
margin: 10px 0;
border-radius: 5px;
border: 1px solid #ccc;
box-sizing: border-box;
`;

const ButtonGroup = styled.div`
display: flex;
flex-direction: column;
width: 100%;

@media (min-width: 576px) {
  flex-direction: row;
  justify-content: space-between;
}
`;

const Button = styled.button`
padding: 10px;
margin: 10px 0;
border: none;
border-radius: 5px;
background-color: #007bff;
color: white;
font-size: 16px;
cursor: pointer;

&:disabled {
  background-color: #ccc;
  cursor: not-allowed;
}

@media (min-width: 576px) {
  &:first-child {
    margin-right: 10px;
  }
}
`;

const RadioButton = styled.div`
display: flex;
align-items: center;
margin: 10px 0;

label {
  margin-left: 5px;
}
`;
const CheckBoxContainer = styled.label`
display: block;
margin: 10px;
cursor: pointer;
font-size: 1rem;

input {
  margin-right: 5px;
}

@media (min-width: 576px) {
  display: flex;
  align-items: center;
  justify-content: center;
}
`

const RadioGroup = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;

@media (min-width: 576px) {
  flex-direction: row;
}
`

const CategoryContainer = styled.div`
display: grid;
grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
gap: 10px;
margin-top: 20px;
width: 100%;
`

const Create = ({ cancel, setGameData, setGamePlaying }) => {
  const [name, setName] = useState('');
  const [rounds, setRounds] = useState("5");
  const [scoringType, setScoringType] = useState("ai");
  const [categories, setCategories] = useState({
    Name: true,
    Place: true,
    Animal: true,
    Thing: true,
    Songs: false,
    Movies: false,
    "TV shows": false,
    Fruits: false,
    //Vegetables: false,
    Books: false,
    //Subjects: false,
    Celebrities: false,
    Musicians: false,
    Instruments: false
  })

  const generateRandomCode = (length) => {
    let code = '';
    for (let i = 0; i < length; i++) {
        code += Math.floor(Math.random() * 10); // Generate a random digit
    }
    return code;
  }

  // Monzo easter egg
  useEffect(() => {
    if (name === "Monzo") {
      setCategories(Object.assign({}, categories, {
        "Office Dog Breeds": false,
        "Meeting Rooms": false,
        "Badge Types": false,
        "Activity Types": false,
        "Engineers": false,
        "Data Scientists": false
      }))
    }

  }, [name])

  const categoriesArray = Object.keys(categories);

  const options = ["5", "7", "10"]
  const gameData = {
    name, isAdmin: true
  }

  const handleCreateGame = (code) => {
    let cats = [];
    categoriesArray.forEach(cat => {
      if (categories[cat]) cats.push(cat);
    });

    socket.emit('create', { name, code, rounds, categories: cats, scoringType }, ({ error, users }) => {
      if (error) {

        alert(error);
      } else {
        gameData.code = code;
        gameData.users = users;
        gameData.maxRounds = Number(rounds);
        gameData.categories = cats;
        setGameData(gameData)
        setGamePlaying(true)
      }
    });
  }

  let disabled = !name || !checkAtleastOneSelected(categoriesArray, categories)

  return (
    <FormContainer>
      <form>
        <FlexColumn>
          {/* Name input and other form fields */}

          {/* Number of rounds */}
          <h2>Select number of rounds:</h2>
          <RadioGroup>
            {options.map(option => (
              <CheckBoxContainer key={option}>
                <input type="radio" id={option} name="rounds" value={option} onChange={(event) => setRounds(event.target.value)} checked={rounds === option} />
                {option}
              </CheckBoxContainer>
            ))}
          </RadioGroup>

          {/* Categories */}
          <h2>Select categories:</h2>
          <CategoryContainer>
            {categoriesArray.map(cat => (
              <CheckBoxContainer key={cat}>
                <input type="checkbox" id={cat} name="categories" onChange={(event) => {
                  setCategories({ ...categories, [cat]: event.target.checked })
                }} checked={categories[cat]} />
                {cat}
              </CheckBoxContainer>
            ))}
          </CategoryContainer>

          {/* Scoring rules */}
          <h2>Scoring Rules:</h2>
          <RadioGroup>
            <CheckBoxContainer>
              <input type="radio" id="cross" name="scoring" value="cross" onChange={(event) => setScoringType(event.target.value)} checked={scoringType === "cross"} />
              Score Each Other
            </CheckBoxContainer>
            <CheckBoxContainer key="self">
              <input type="radio" id="self" name="scoring" value="self" onChange={(event) => setScoringType(event.target.value)} checked={scoringType === "self"} />
              Score Yourself
            </CheckBoxContainer>
            <CheckBoxContainer key="ai">
              <input type="radio" id="ai" name="scoring" value="ai" onChange={(event) => setScoringType(event.target.value)} checked={scoringType === "ai"} />
              Score by AI
            </CheckBoxContainer>
          </RadioGroup>

      <ButtonGroup>
        <Button disabled={disabled} onClick={handleCreateGame}>
          Create Room
        </Button>
        <Button onClick={cancel}>
          Cancel
        </Button>
      </ButtonGroup>
        </FlexColumn>
      </form>
    </FormContainer>
  )
}


const checkAtleastOneSelected = (categoriesList, categories) => {
  let enableButton = false;
  categoriesList.forEach(category => {
    if (categories[category]) {
      enableButton = true;
    }
  })
  return enableButton
}

export default Create
